import React, { createContext, useState, useEffect } from 'react'
import axios from "axios"
import createTicket from "./helpers/createTicket"
import getDays from './helpers/getDays';
const authAxios = axios.create()
const userAxios = axios.create()
export const storeContext = createContext({});

userAxios.interceptors.request.use((config) => {
    const token = localStorage.getItem("token");
    config.headers.Authorization = `Bearer ${token}`;
    return config;
})



const Store = (props) => {
    const [cars, setCars] = useState([]);
    const [hotList, setHotList] = useState([]);
    const [currentCar, setCurrentCar] = useState("");
    const [currentPart, setCurrentPart] = useState("");
    const [showPullForm, setShowPullForm] = useState(false);
    const [showPullTicket, setShowPullTicket] = useState(false);
    const [pullTickets, setPullTickets] = useState([]);
    const [currentPullTicket, setCurrentPullTicket] = useState([]);
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')) || {})
    const [yardFilter, setYardFilter] = useState({
        yard: "All Yards",
        sortBy: "recent",
        hotList: false,
        scroll: 0,
        partSearch: "",
        recommended: true,
    })

    useEffect(() => {
        getCars();
        getHotList();
        getPullTickets();
    }, [user])


    const login = (credentials) => {
        return authAxios.post("/auth/login", credentials)
            .then(response => {
                const { user, user: { token } } = response.data;
                localStorage.setItem("user", JSON.stringify(user))
                localStorage.setItem("token", token)
                setUser(user);
                return response;
            })
    }

    const logout = () => {
        localStorage.removeItem("user");
        localStorage.removeItem("token");
        setUser({});
    }

    return (

        <storeContext.Provider value={{
            //context functions
        }} >
            {props.children}
        </storeContext.Provider >
    )
}

export default Store
