import React, {useState} from "react";
import {Button} from "react-bootstrap"
import Styles from "./Toolbar.module.scss";

const Toolbar = (props) => {
    const [searchKeyword, changeSearchKeyword] = useState
    const {toggleModal, items, changeItems} = props;
    return (
        <div className={Styles.wrapper}>
            <input onChange={(e) => changeSearchKeyword(e.target.value)} type="text" value={searchKeyword} placeholder="Search Keyword" />
            <div className="spacer"></div>
            <Button onClick={() => toggleModal(true)}> Insert New Item</Button>
        </div>
    );
}

export default Toolbar;
