import React from "react";
import Styles from "./Menu.module.scss";

const Menu = (props) => {
    const { title, subMenu } = props;

    function toggleSubMenu(){
        let arrowIcon = document.querySelector(".material-icons");
        let subMenu = document.querySelector("#subMenu");

        if (arrowIcon.textContent === "keyboard_arrow_up"){
            arrowIcon.textContent = "keyboard_arrow_down";
            subMenu.classList = Styles.subMenuOpen
        } else {
            arrowIcon.textContent = "keyboard_arrow_up";
            subMenu.classList = Styles.subMenuClosed
        }

    }

    return (
        <div onClick={() => toggleSubMenu(!subMenuOpen)}
            className={Styles.wrapper}>
                <p>{title}
                    <div className="spacer"></div>
                    <i className="material-icons">{subMenuOpen ? "keyboard_arrow_up" : "keyboard_arrow_down"}
                    </i>
                </p>
            <div id="subMenu" className={Styles.subMenuClosed}>
                {subMenu}
            </div>
        </div>
    );
}

export default Menu;
