import React, { useState } from "react";
import Styles from "./Menu.module.scss";
import SubMenu from "./SubMenu/SubMenu"

const Menu = (props) => {
    const { title, subMenu } = props;

    const [subMenuOpen, toggleSubMenu] = useState(false)

    return (
        <div onClick={() => toggleSubMenu(!subMenuOpen)}
            className={Styles.wrapper}>
            <div className={Styles.title}>
                <p>{title}</p>
                <div className="spacer"></div>
                <i className="material-icons">{subMenuOpen ? "keyboard_arrow_up" : "keyboard_arrow_down"}</i>
            </div>
            {subMenuOpen && <SubMenu open={subMenuOpen} titles={subMenu} />}
        </div>
    );
}

export default Menu;
