import React, { useState } from "react";
import Styles from "./Menu.module.scss";

const Menu = (props) => {
    const { title, subMenu } = props;

    const [subMenuOpen, toggleSubMenu] = useState(false)

    return (
        <div onClick={() => toggleSubMenu(!subMenuOpen)}
            className={Styles.wrapper}>
                <p>{title}
                    <div className="spacer"></div>
                    <i className="material-icons">{subMenuOpen ? "keyboard_arrow_up" : "keyboard_arrow_down"}

                    </i>
                </p>
            <div className={Styles.subMenu}>
                {subMenu}
            </div>
        </div>
    );
}

export default Menu;
